(68, 'Diploma  Project Selected Area', NULL, 'diploma-project-selected-area', '<ol>\r\n<li>Architecture and Interior Design Technology</li>\r\n<li>Civil Technology</li>\r\n<li>Computer Technology</li>\r\n<li>Construction Technology</li>\r\n<li>Electrical Technology</li>\r\n<li>Electronics Technology</li>\r\n<li>Garment Design and Pattern Making</li>\r\n<li>Marin Technology</li>\r\n<li>Mechanical Technology</li>\r\n<li>Refrigeration and Air Conditioning Technology</li>\r\n<li>Food Technology</li>\r\n<li>Environmental Technology</li>\r\n<li>Tourism &amp; Hospitality</li>\r\n<li>Automobile Technology</li>\r\n<li>Electro-Medical</li>\r\n<li>Instrumentation and Process Control Technology</li>\r\n<li>Power Technology</li>\r\n<li>IPCT Technology</li>\r\n<li>Telecommunication Technology</li>\r\n<li>Ceramic Technology</li>\r\n<li>Textile Engineering</li>\r\n<li>Agriculture</li>\r\n</ol>', NULL, NULL, NULL, 'publish', NULL, 'page', 'four-year-diploma-scholarship', 'new', NULL, 1, NULL, NULL, NULL, 0, 1, '2019-10-21 07:55:36', '2019-10-21 07:57:21'),
(69, 'Diploma  Project Financial Assistance', NULL, 'diploma-project-financial-assistance', '<p>Provide 4-Year or 48 months Diploma education expenses for Dakhil (Vocational) graduates under IDB-BISEW Madrasah Project in the following areas:</p>\r\n<ol>\r\n<li>Tuition Fees</li>\r\n<li>Book &amp; Stationary Expenses</li>\r\n<li>Food &amp; Lodging Expenses</li>\r\n</ol>', NULL, 'Provide 4-Year or 48 months Diploma education expenses for Dakhil (Vocational) graduates under IDB-BISEW Madrasah Project in the following areas.', NULL, 'publish', NULL, 'page', 'four-year-diploma-scholarship', 'from_old', NULL, 1, NULL, NULL, NULL, 0, 1, '2019-10-21 07:59:49', '2019-10-21 08:00:46'),
(70, 'Diploma  Project Job Placement Assistance', NULL, 'diploma-project-job-placement-assistance', '<p>After Succesfully completion of Diploma Engineering IsDB-BISEW will provide job placement service.</p>', NULL, 'After Succesfully completion of Diploma Engineering IsDB-BISEW will provide job placement service.', NULL, 'publish', NULL, 'page', 'four-year-diploma-scholarship', 'from_old', NULL, 1, NULL, NULL, NULL, 0, 1, '2019-10-21 08:03:19', '2019-10-21 08:03:32'),
